<style>
    svg {
        height: 31rem;
    }
    @keyframes pencil-ani{
        0%{stroke-dashoffset: 23;}

        50%{
            stroke-dashoffset: 23;
        }
        100% {stroke-dashoffset: 0;}
    }
    @keyframes move-ani{
        0%{
            transform:translate(0px,0px);
        }
         55%{
            
            transform:translate(-35px, 16px)
            rotate(-8deg);
            
            
         }
        100%{
            
            transform:translate(-17px, 12px)
            rotate(-8deg);
           
        }
    }

    .pencil-line{
    fill: none;
    stroke-width: 0.5px;
    stroke-linejoin: round;
    stroke-linecap: round;
    stroke: #222;
    stroke-width: 1px;
    stroke-dasharray: 23px;
    animation: pencil-ani 5s infinite;
    }
    .Dog-pencil-hand{
        animation: move-ani 5s cubic-bezier(0.26,0.21,0.37,1.3) infinite;
        
    }
</style>
